import*as GraphStyle from'./GraphStyle.js';export const calculateInputPortXY=portIndex=>{const y=GraphStyle.InputPortRadius+GraphStyle.LeftSideTopPadding+portIndex*GraphStyle.TotalInputPortHeight;return{x:0,y:y};};export const calculateOutputPortXY=(portIndex,nodeSize,numberOfOutputs)=>{const{width,height}=nodeSize;const outputPortY=(height/2)+(2*portIndex-numberOfOutputs+1)*GraphStyle.TotalOutputPortHeight/2;return{x:width,y:outputPortY};};export const calculateParamPortXY=(portIndex,offsetY)=>{const paramPortY=offsetY+GraphStyle.TotalParamPortHeight*(portIndex+1)-GraphStyle.AudioParamRadius;return{x:0,y:paramPortY};};